CREATE TABLE "users" (
  "id" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY NOT NULL,
  "first_name" VARCHAR(50) NOT NULL,
  "last_name" VARCHAR(50) NOT NULL,
  "email" VARCHAR(250) UNIQUE NOT NULL,
  "password" VARCHAR(250) NOT NULL,
  "status" VARCHAR(50) NOT NULL,
  "created" TIMESTAMP NOT NULL DEFAULT (now()),
  "updated" TIMESTAMP NOT NULL DEFAULT (now()),
  "deleted" TIMESTAMP,
  "is_superuser" BOOLEAN DEFAULT FALSE
);

CREATE TABLE "teams" (
  "id" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY NOT NULL,
  "name" VARCHAR(100) UNIQUE NOT NULL,
  "desc" TEXT NOT NULL,
  "status" VARCHAR(50) NOT NULL,
  "created" TIMESTAMP NOT NULL DEFAULT (now()),
  "updated" TIMESTAMP NOT NULL DEFAULT (now()),
  "deleted" TIMESTAMP
);

CREATE TABLE "roles" (
  "id" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY NOT NULL,
  "name" VARCHAR(100) UNIQUE NOT NULL,
  "desc" TEXT NOT NULL,
  "status" VARCHAR(50) NOT NULL,
  "created" TIMESTAMP NOT NULL DEFAULT (now()),
  "updated" TIMESTAMP NOT NULL DEFAULT (now()),
  "deleted" TIMESTAMP
);

CREATE TABLE "projects" (
  "id" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY NOT NULL,
  "name" VARCHAR(100) UNIQUE NOT NULL,
  "desc" TEXT NOT NULL,
  "status" VARCHAR(50) NOT NULL,
  "created" TIMESTAMP NOT NULL DEFAULT (now()),
  "updated" TIMESTAMP NOT NULL DEFAULT (now()),
  "deleted" TIMESTAMP,
  "start_date" TIMESTAMP,
  "end_date" TIMESTAMP
);

CREATE TABLE "users_audit" (
  "id" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY NOT NULL,
  "updated_by" INTEGER NOT NULL,
  "updated_at" TIMESTAMP NOT NULL DEFAULT (now()),
  "user_id" INTEGER NOT NULL,
  "action" VARCHAR(250) NOT NULL,
  "details" JSONB NOT NULL
);
ALTER TABLE "users_audit" ADD FOREIGN KEY ("updated_by") REFERENCES "users" ("id");
ALTER TABLE "users_audit" ADD FOREIGN KEY ("user_id") REFERENCES "users" ("id");

CREATE TABLE "projects_audit" (
  "id" INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY NOT NULL,
  "updated_by" INTEGER NOT NULL,
  "updated_at" TIMESTAMP NOT NULL DEFAULT (now()),
  "project_id" INTEGER NOT NULL,
  "action" VARCHAR(250) NOT NULL,
  "details" JSONB NOT NULL
);
ALTER TABLE "projects_audit" ADD FOREIGN KEY ("updated_by") REFERENCES "users" ("id");
ALTER TABLE "projects_audit" ADD FOREIGN KEY ("project_id") REFERENCES "projects" ("id");

CREATE TABLE "users_projects_roles" (
  "user_id" INTEGER NOT NULL,
  "project_id" INTEGER NOT NULL,
  "role_id" INTEGER NOT NULL
);
ALTER TABLE "users_projects_roles" ADD PRIMARY KEY ("user_id", "project_id", "role_id");
ALTER TABLE "users_projects_roles" ADD FOREIGN KEY ("user_id") REFERENCES "users" ("id");
ALTER TABLE "users_projects_roles" ADD FOREIGN KEY ("project_id") REFERENCES "projects" ("id");
ALTER TABLE "users_projects_roles" ADD FOREIGN KEY ("role_id") REFERENCES "roles" ("id");

CREATE TABLE "teams_projects" (
  "team_id" INTEGER NOT NULL,
  "project_id" INTEGER NOT NULL
);
ALTER TABLE "teams_projects" ADD PRIMARY KEY ("team_id", "project_id");
ALTER TABLE "teams_projects" ADD FOREIGN KEY ("team_id") REFERENCES "teams" ("id");
ALTER TABLE "teams_projects" ADD FOREIGN KEY ("project_id") REFERENCES "projects" ("id");
